import React, { useState, useRef } from "react"
import emailjs from "@emailjs/browser"
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import { response } from "express";

function ContactForm() {
    const [validated, setValidated] = useState(false)
    const form = useRef();

    const sendEmail = (e) => {
        e.preventDefault();

        emailjs
            .sendForm(
                process.env.REACT_APP_EMAILJS_SERVICE_I,
                process.env.REACT_APP_EMAILJS_TEMPLATE_ID,
                form.current,
                process.env.REACT_APP_EMAILJS_PUBLIC_KEY
            )
            .then(
                (response) => {
                response.send("Your message was sent")
                setValidated(false)
                e.target.reset()
            }, 
                (error) => {
                    response.send("Message not sent", error)
                } 
            )
    };

    const handleSubmit = (e) => {
        const form = e.currrentTarget
        if (form.checkValidity() === false) {
            e.preventDefault()
            e.stopPropagation()
        } else {
            sendEmail(e)
        }
        setValidated(true)
        }
    return (

        <Form noValidate ref={form} onSubmit={handleSubmit} validation={validated}>
            <Row className="mb-3"> 
                <Col md lg="3">
                    <Form.Group  as={Col}  controlId="formGridEmail">
                        <Form.Label>Name</Form.Label>
                        <Form.Control  type="text" placeholder="Full Name" />
                    </Form.Group>
                </Col>
                <Col md lg="3">
                <Form.Group as={Col}  controlId="formGridEmail">
                    <Form.Label>Email</Form.Label>
                    <Form.Control type="email" placeholder="Email" />
                    </Form.Group>
                </Col>
            </Row>

            <Form.Group  className="mb-3" controlId="exampleForm.ControlTextarea1">
                <Form.Label>Your messesage</Form.Label>
                <Form.Control as="textarea" style={{ height: '110px', width: "50%" }}
/>
            </Form.Group>

            <Button variant="primary" type="submit">
                Submit
            </Button>
            </Form>

    )
}
export default ContactForm
