import React, {useRef, useState} from "react"
import { Form, Col, Button, Row } from 'react-bootstrap';
import emailjs from "@emailjs/browser"
// import { EmailError, EmailSent } from "./formResponse";

const initialFormData = Object.freeze({
    name: "",
    email: "",
    message: ""
})

function ContactForm() {
    const form = useRef();

    const [formData, updateFormData] = useState(initialFormData)
    const[errorMessage, setErrorMessage] = useState('')

    const handleErrorMessage = () => {
        setErrorMessage('Message was not sent')
    }
    const handleChange = (e) => {
        updateFormData({
            ...formData,
            [e.target.name]: e.target.value.trim()
        })
    }
    const handleSubmit = (e) => {
        e.preventDefault()

        emailjs.sendForm(process.env.REACT_APP_EMAILJS_SERVICE_I, process.env.REACT_APP_EMAILJS_TEMPLATE_ID, form.current, process.env.REACT_APP_EMAILJS_PUBLIC_KEY)
            .then((result) => {

                console.log(result.text);
            }, (error) => {
                <p className="errorMessage"> {handleErrorMessage} </p>
                console.log(error.text);
            });
    }

    return (

        <Form ref={form}>
            <Row className="mb-3"> 
                <Col md lg="3">
                    <Form.Group  as={Col}  controlId="formGridEmail">
                        <Form.Label>Name</Form.Label>
                        <Form.Control onChange={handleChange} name="name" type="text" placeholder="Full Name" />
                    </Form.Group>
                </Col>
                <Col md lg="3">
                <Form.Group as={Col}  controlId="formGridEmail">
                    <Form.Label>Email</Form.Label>
                        <Form.Control onChange={handleChange} name="email" type="email" placeholder="Email" />
                    </Form.Group>
                </Col>
            </Row>

            <Form.Group  className="mb-3" controlId="exampleForm.ControlTextarea1">
                <Form.Label>Your messesage</Form.Label>
                <Form.Control onChange={handleChange} name="message" as="textarea" style={{ height: '110px', width: "50%" }} />
            </Form.Group>

            <Button onClick={handleSubmit} variant="primary" type="submit">
                Submit
            </Button>
            {errorMessage && <div className="errorMessage"> {errorMessage} </div>}
        </Form>

    )
}
export default ContactForm
