import React, { useState, useRef } from "react";
import emailjs from "@emailjs/browser"
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';


function NewForm() {
    const form = useRef()

    // const [status, setStatus] = useState()
    const [submitted, setSubmitted] = useState(false)

    const handleSubmit = (e) => {
        e.preventDefault();

        emailjs.sendForm(process.env.REACT_APP_EMAILJS_SERVICE_I, process.env.REACT_APP_EMAILJS_TEMPLATE_ID, form.current, process.env.REACT_APP_EMAILJS_PUBLIC_KEY)
            .then((result) => {
                alert(result.text);
            }, (error) => {
                alert(error.text);
            });

        setSubmitted(true)
    }


    return (
        <div>
            <Form 
            ref={form}
            onSubmit={handleSubmit}
            >
            <Row className="mb-3">
                    <Col md lg="3">
                        <Form.Group as={Col} controlId="formGridEmail">
                            <Form.Label>Name</Form.Label>
                            <Form.Control type="text" placeholder="Full Name"/>
                        </Form.Group>
                    </Col>
                <Col md lg="3">
                    <Form.Group as={Col} controlId="formGridEmail">
                        <Form.Label>Email</Form.Label>
                        <Form.Control type="email" placeholder="Email" />
                    </Form.Group>
                </Col>
            </Row>

            <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
                <Form.Label>Your messesage</Form.Label>
                <Form.Control as="textarea" style={{ height: '110px', width: "50%" }}
                />
            </Form.Group>

                <Button type="submit">
                Submit
                </Button>
                {submitted}
            </Form>
        </div>
)
}
export default NewForm
