import React, { useRef, useState } from 'react';
import { createPopper } from '@popperjs/core';
import emailjs from "@emailjs/browser"
import { Button, Col, Form, Overlay, Row } from 'react-bootstrap';


function FormFunctionality() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');
    const [emailSent, setEmailSent] = useState(false)

    const [show, setShow] = useState(false)
    const { form, target } = useRef();

    createPopper({
        placement: 'bottom',
        color: 'orange'
    })
    const submit = () => {
        if (name && email && message) {
            const serviceId = process.env.REACT_APP_EMAILJS_SERVICE_ID
            const templateId = process.env.REACT_APP_EMAILJS_TEMPLATE_ID
            const userId = process.env.REACT_APP_EMAILJS_PUBLIC_KEY
            const templateParams = {
                name,
                email,
                message
            }

            emailjs.send(serviceId, templateId, templateParams, userId)
                .then(response => console.log(response))
                .then(error => console.log(error))

            setName('')
            setEmail('')
            setMessage('')
            setEmailSent(true)
        } else {
            alert('Please fill all fields')  
        }
    }

    return (
        <Form ref={form} onSubmit={submit}>
            <Row className="mb-3">
                <Col md lg="3">
                    <Form.Group as={Col} controlId={name}>
                        <Form.Label>Name</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Full Name" 
                            onChange={e => setName(e.target.value)}
                            />
                    </Form.Group>
                </Col>
                <Col md lg="3">
                    <Form.Group as={Col} controlId={email}>
                        <Form.Label>Email</Form.Label>
                        <Form.Control
                            type="email"
                            onChange={e => setEmail(e.target.value)}
                            placeholder="Email" />
                    </Form.Group>
                </Col>
            </Row>

            <Form.Group className="mb-3" controlId={message}>
                <Form.Label>Your messesage</Form.Label>
                <Form.Control
                    as="textarea"
                    onChange={e => setMessage(e.target.value)}
                    style={{ height: '110px', width: "50%" }}
                />
            </Form.Group>

            <Button type="submit" ref={target} onClick={() => setShow(!show)}>
            Submit
            </Button>
            <Overlay target={target} show={emailSent ? show : null} placement="bottom">
                {(createPopper)=>
                <div style={{ createPopper }}>
                    Message Sent
            </div>
            } 
            </Overlay>
        </Form>
)
}
export default FormFunctionality 
