import React, { useRef, useState } from 'react';
import emailjs from "@emailjs/browser"
import { Button, Col, Form, Row } from 'react-bootstrap';

function FormFunctionality() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');
    const [emailSent, setEmailSent] = useState(false)

    const form = useRef();

    function submitForm(e) {
        e.preventDefault();

        emailjs.sendForm(process.env.REACT_APP_EMAILJS_SERVICE_I, process.env.REACT_APP_EMAILJS_TEMPLATE_ID, form.current, process.env.REACT_APP_EMAILJS_PUBLIC_KEY)
            .then((result) => {
                setFormStatus("Sent")
                console.log(result.text);
            }, (error) => {
                setFormStatus("Error")
                console.log(error.text);
            });

        const submit = () => {
            if (name && email && message) {
                setName('')
                setEmail('')
                setMessage('')
                setEmailSent('')
            } else {
                alert('Please fill all fields')
        }
    }
    }

    return (
        <Form ref={form} onSubmit={submitForm}>
            <Row className="mb-3">
                <Col md lg="3">
                    <Form.Group as={Col} controlId={name}>
                        <Form.Label>Name</Form.Label>
                        <Form.Control type="text" placeholder="Full Name" />
                    </Form.Group>
                </Col>
                <Col md lg="3">
                    <Form.Group as={Col} controlId={email}>
                        <Form.Label>Email</Form.Label>
                        <Form.Control type="email" placeholder="Email" />
                    </Form.Group>
                </Col>
            </Row>

            <Form.Group className="mb-3" controlId={message}>
                <Form.Label>Your messesage</Form.Label>
                <Form.Control as="textarea" style={{ height: '110px', width: "50%" }}
                />
            </Form.Group>

            <Button
                type="submit"
                onClick={submit}
            >

            </Button>
            <span className={emailSent ? 'visible' : null}>Thank you for your message, we will be in touch in no time!</span>

        </Form>
)
}
export default FormFunctionality
