import React, { useRef, useState } from "react"
import emailjs from "@emailjs/browser"
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';

function ContactForm() {
    const [sentMessage, setSentMessage] = useState('')
    const [errorMessage, setErrorMessage] = useState('')

    function handleSent() {
        setSentMessage('Your message was sent successfully.')
    }
    function handleError() {
        setErrorMessage('Sorry, an error occured your message was not sent.')
    }

    const form = useRef();

    const sendEmail = (e) => {
        e.preventDefault();

        emailjs.sendForm(process.env.REACT_APP_EMAILJS_SERVICE_ID, process.env.REACT_APP_EMAILJS_TEMPLATE_ID, form.current, process.env.REACT_APP_EMAILJS_PUBLIC_KEY)
            .then((result) => {

                console.log(result.text);
            }, (error) => {
                <p>{errorMessage}</p>
                console.log(error.text);
            });
    };
    return (

        <Form ref={form} onSubmit={sendEmail}>
            <Row className="mb-3"> 
                <Col md lg="3">
                    <Form.Group  as={Col}  controlId="formGridEmail">
                        <Form.Label>Name</Form.Label>
                        <Form.Control  type="text" placeholder="Full Name" />
                    </Form.Group>
                </Col>
                <Col md lg="3">
                <Form.Group as={Col}  controlId="formGridEmail">
                    <Form.Label>Email</Form.Label>
                    <Form.Control type="email" placeholder="Email" />
                    </Form.Group>
                </Col>
            </Row>

            <Form.Group  className="mb-3" controlId="exampleForm.ControlTextarea1">
                <Form.Label>Your messesage</Form.Label>
                <Form.Control as="textarea" style={{ height: '110px', width: "50%" }}
/>
            </Form.Group>

            <Button variant="primary" type="submit">
                Submit
            </Button>
            </Form>

    )
}
export default ContactForm
