import React, { useRef } from "react"
import emailjs from "@emailjs/browser"
import Header from "./header"

function Contact() {
    const form = useRef()

    const sendEmail = (e) => {
        e.preventDefault();

        emailjs.sendForm("REACT_APP_EMAILJS_SERVICE_ID", "REACT_APP_EMAILJS_TEMPLATE_ID", form.current, REACT_APP_EMAILJS_PUBLIC_KEY)
            .then((result) => {
                console.log(result.text)
            }, (error) => {
                console.log(error.text)
            })
    }
    return (
        <div className="contactPage">
            <Header />
            <div className="flex-container">
                <h4>Contact Me</h4> 

            <div className="contactForm form-group">
            <form ref={form} onSubmit={sendEmail}>
                <label>Name</label>
                <input type="text" className="user_name"/>
                <label>Email</label>
                        <input type="email" className="user_email" />
                <label>Message</label>
                        <textarea className="message" />
                <button type="submit"> Send </button>
                </form>
            </div>
            </div>
        </div>
    )
}

export default Contact
