import React, { useRef, useState } from 'react';
import emailjs from "@emailjs/browser"
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';

function FormFunctionality () {
    const [formStatus, setFormStatus] = useState('')
    const form = useRef();

    function submitForm(e) {
        e.preventDefault();

        emailjs.sendForm(process.env.REACT_APP_EMAILJS_SERVICE_I, process.env.REACT_APP_EMAILJS_TEMPLATE_ID, form.current, process.env.REACT_APP_EMAILJS_PUBLIC_KEY)
            .then((result) => {
                setFormStatus("Sent")
                console.log(result.text);
            }, (error) => {
                setFormStatus("Error")
                console.log(error.text);
            });
    }
     // const { formGridName, formGridEmail, ControlTextarea1 } = e.target.elements

        // let conForm = {
        //     name: formGridName.value,
        //     email: formGridEmail.value,
        //     message: ControlTextarea1.value
        // }


    return (
        <Form ref={form} onSubmit={submitForm}>
            <Row className="mb-3">
                <Col md lg="3">
                    <Form.Group as={Col} controlId="formGridName">
                        <Form.Label>Name</Form.Label>
                        <Form.Control type="text" placeholder="Full Name" />
                    </Form.Group>
                </Col>
                <Col md lg="3">
                    <Form.Group as={Col} controlId="formGridEmail">
                        <Form.Label>Email</Form.Label>
                        <Form.Control type="email" placeholder="Email" />
                    </Form.Group>
                </Col>
            </Row>

            <Form.Group className="mb-3" controlId="ControlTextarea1">
                <Form.Label>Your messesage</Form.Label>
                <Form.Control as="textarea" style={{ height: '110px', width: "50%" }}
                />
            </Form.Group>

            <Button type="submit">
                {formStatus}
            </Button>
        </Form>
)
}
export default FormFunctionality
