import React, {useState} from "react"
import { Form, Col, Button, Row } from 'react-bootstrap';

const initialFormData = Object.freeze({
    name: "",
    email: "",
    message: ""
})

function ContactForm() {
    const [formData, updateFormData] = useState(initialFormData)

    const sendFeedback = (serviceID, templateId, variables) => {
        window.emailjs.send(
            serviceID,
            templateId,
            variables
        ).then(res => {
            console.log('Email sent!');
        })
        .catch(err => console.error('Error occured, message not sent'))
    }

    const handleChange = (e) => {
        updateFormData({
            ...formData,
            [e.target.name]: e.target.value.trim()
        })
    }
    const handleSubmit = (e) => {
        e.preventDefault()
        alert(`Your message has been sent.`);
        const templateId = process.env.REACT_APP_EMAILJS_TEMPLATE_ID;
        const serviceID = process.env.REACT_APP_EMAILJS_SERVICE_ID;
        sendFeedback(serviceID, templateId, { from_name: formData.name, email: formData.email, message: formData.message })

        console.log(formData);
    }

    return (

        <Form>
            <Row className="mb-3"> 
                <Col md lg="3">
                    <Form.Group  as={Col}  controlId="formGridEmail">
                        <Form.Label>Name</Form.Label>
                        <Form.Control onChange={handleChange} name="name" type="text" placeholder="Full Name" />
                    </Form.Group>
                </Col>
                <Col md lg="3">
                <Form.Group as={Col}  controlId="formGridEmail">
                    <Form.Label>Email</Form.Label>
                        <Form.Control onChange={handleChange} name="email" type="email" placeholder="Email" />
                    </Form.Group>
                </Col>
            </Row>

            <Form.Group  className="mb-3" controlId="exampleForm.ControlTextarea1">
                <Form.Label>Your messesage</Form.Label>
                <Form.Control onChange={handleChange} name="message" as="textarea" style={{ height: '110px', width: "50%" }} />
            </Form.Group>

            <Button onClick={handleSubmit} variant="primary" type="submit">
                Submit
            </Button>
        </Form>

    )
}
export default ContactForm
